(()=>{"use strict";(e=>{const t=document.getElementById("timer-hours"),n=document.getElementById("timer-minutes"),s=document.getElementById("timer-seconds");let o;o=setInterval((()=>{let e=(()=>{let e=(new Date("25 april 2022").getTime()-(new Date).getTime())/1e3,t=Math.floor(e/3600),n=Math.floor(e/60%60),s=Math.floor(e%60);return e<=0&&(e=0,t=0,n=0,s=0),{timeRemaining:e,hours:t,minutes:n,seconds:s}})();t.textContent=e.hours<10?"0"+e.hours:e.hours,n.textContent=e.minutes<10?"0"+e.minutes:e.minutes,s.textContent=e.seconds<10?"0"+e.seconds:e.seconds,e.timeRemaining<=0&&clearInterval(o)}),1e3)})()})();